import {createSlice, PayloadAction} from "@reduxjs/toolkit";
import {IWare} from "../../types/ware";

interface IWareReducerState{
    wares: string[],
    // wares: IWare[],
    isLoading: boolean,
    error: string,
}

const initialState: IWareReducerState = {
    wares: [],
    isLoading: false,
    error: "",
}

export const wareReducer = createSlice({
    initialState: initialState,
    name: "userReducer",
    reducers: {
        GET_WARE(state){
            state.isLoading = true
            state.error = ""
        },
        GET_WARE_SUCCESS(state, action: PayloadAction<string[]>){
            state.wares = action.payload
            state.isLoading = false
            state.error = ""
        },
        GET_WARE_ERROR(state, action: PayloadAction<string>){
            state.isLoading = false
            state.error = action.payload
        },
    }
})
export default wareReducer.reducer
