import React, {useEffect} from 'react';
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {fetch_discount_wares} from "../../redux/action_creators/ware_action_creator";

const Discounts = () => {
    const dispatch = useAppDispatch()
    const {error,isLoading,wares} = useAppSelector(state => state.wareReducer)
    useEffect(() => {
        dispatch(fetch_discount_wares())
    }, [ ])
    return (
        <div className="container mt-5 d-block">
            <div className="d-block">
                <h2 className="d-xxl-inline text-center text-xxl-start fw-bold border-3 border-dark border-bottom">DISCOUNTS</h2>
            </div>
            <div className="text-center text-xxl-start d-table">
                {wares?.map(item =>
                    <div className="card shadow mt-4 me-xxl-4 d-table-cell" style={{"width": "18rem"}}>
                        <p className="position-absolute top-0 start-0 bg-danger px-2 py-1 m-1 text-white">10%</p>
                        <img
                            src="https://www.staff-clothes.com/_next/image/?url=https%3A%2F%2Fstatic.staff-clothes.com%2Fmedia%2Fcache%2Fimage_product_desktop_catalog%2Fimage_product%2F0002%2F73%2Fe3b0187358014ffeadec729c7f0beebf.jpeg&w=1920&q=75"
                            className="card-img-top" alt="..."/>
                        <div className="card-body">
                            <h5 className="card-title bg-dark"><p className={'d-inline'}>{item.name}</p></h5>
                            <p className="card-text text-muted">{item.sizes}</p>
                            <p className="text-danger d-inline">{item.price} грн.</p>
                            <p className="text-muted d-inline">
                                <del>{item.oldPrice} грн.</del>
                            </p>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Discounts;
