import {AppDispatch} from "../store";
import {authorizationReducer} from "../reducers/authorizationReducer";
import axios from "axios";
import {GraphQlEndpoint} from "../../global_variables";

export const login = () => async (dispatch: AppDispatch) => {
    dispatch(authorizationReducer.actions.LOGIN())
    try{
        const response = await axios.get<any>(GraphQlEndpoint, {
            data: `
                    query login{
            authorization{
              login(userLogin: {
                username: "nomoremikolo"
                password: "123123"
              }){
                errors
                refreshToken
                statusCode
                token
                user{
                  id
                  username
                  name
                  surname
                  age
                  email
                  adress
                  role
                  isActivated
                  permissions
                }
              }
            }
          }
             `
        })
        console.log(response)
    }
    catch (e) {
        console.log(e)
    }
}
