import {AppDispatch} from "../store";
import {authorizationReducer} from "../reducers/authorizationReducer";
import axios from "axios";
import {GraphQlEndpoint} from "../../global_variables";
import {usersReducer} from "../reducers/usersReducer";
import {NotificationReducer} from "../reducers/NotificationReducer";
import {IUpdatedUser} from "../../types/user";
const {SHOW_SUCCESS_MESSAGE, SHOW_ERROR_MESSAGE, SHOW_WARNING_MESSAGE, DEACTIVATE_MESSAGE} = NotificationReducer.actions
export const fetch_all_users = () => async (dispatch: AppDispatch) => {
    const graphqlQuery = {
        "query": `
            query getAll {
              user {
                getAll {
                  errors
                  statusCode
                  users {
                    id
                    username
                    name
                    surname
                    age
                    email
                    adress
                    role
                    isActivated
                    permissions
                  }
                }
              }
            }
        `,
    };
    dispatch(usersReducer.actions.GET_ALL_USERS())
    try {
        const response = await axios({
            url: GraphQlEndpoint,
            method: 'post',
            withCredentials: true,
            headers: {
                "content-type": "application/json",
                "Authorization": `Bearer ${localStorage.getItem('accessToken')}`,
            },
            data: graphqlQuery
        })
        let r = response.data.data.user.getAll
        if (r.statusCode === 200){
            dispatch(usersReducer.actions.GET_ALL_USERS_SUCCESS(r.users))
        }else{
            dispatch(usersReducer.actions.GET_ALL_USERS_ERROR(r.errors))
        }
    } catch (e) {
        dispatch(usersReducer.actions.GET_ALL_USERS_ERROR(e))
        localStorage.removeItem('accessToken')
    }
}

export const update_users = (user: IUpdatedUser) => async (dispatch: AppDispatch) => {
    const graphqlQuery = {
        "query": `
            mutation updateU{
              user{
                updateUser(user: {
                  id: 9,
                  name: "Mykola"
                  surname: "Kutsko",
                  username: "nomoremikolo",
                  role: "lox",
                  isActivated: true,
                  age: 18,
                }){
                  errors
                  statusCode
                  user{
                    id
                    username
                    name
                    surname
                    age
                    email
                    adress
                    role
                    isActivated
                    permissions
                  }
                }
              }
            }
        `,
    };
    dispatch(usersReducer.actions.GET_ALL_USERS())
    try {
        const response = await axios({
            url: GraphQlEndpoint,
            method: 'post',
            withCredentials: true,
            headers: {
                "content-type": "application/json",
                "Authorization": `Bearer ${localStorage.getItem('accessToken')}`,
            },
            data: graphqlQuery
        })
        let r = response.data.data.user.updateUser
        if (r.statusCode === 200){
            dispatch(SHOW_SUCCESS_MESSAGE("Successfully updated!"))
            dispatch(usersReducer.actions.GET_ALL_USERS_SUCCESS(r.users))
        }else{
            dispatch(SHOW_ERROR_MESSAGE(r.errors))
            dispatch(usersReducer.actions.GET_ALL_USERS_ERROR(r.errors))
        }
    } catch (e) {
        dispatch(usersReducer.actions.GET_ALL_USERS_ERROR(e))
        localStorage.removeItem('accessToken')
    }
}
