import React, {useEffect, useState} from 'react';
import {useForm} from "react-hook-form";
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {fetch_all_brands, fetch_all_categories} from "../../redux/action_creators/ware_action_creator";
interface base{
    item: string | base | ArrayBuffer | null
}
const NewWarePage = () => {
    const dispatch = useAppDispatch()
    const categories = useAppSelector(state => state.wareReducer.allCategories.categories)
    const brands = useAppSelector(state => state.wareReducer.allBrands.brands)
    const [thumbnail, setThumbnail] = useState<ArrayBuffer | null | string>("");
    const [images, setImages] =  useState<any>([]);
    const {
        register,
        formState: {
            errors,
        },
        handleSubmit,
    } = useForm()
    const submitHandler = () => {

    }
    const selectThumbnailHandler = (e:any) => {
        let file = e.target.files[0]
        console.log(file)
        let reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = function () {
            // console.log(reader.result);
            setThumbnail(reader.result)
        };
        reader.onerror = function (error) {
            console.log('Error: ', error);
        };
    }
    const selectImagesHandler = async (e: React.ChangeEvent<HTMLInputElement>) => {
        let files = e.target.files
        let line = []

        for (let i = 0; i < files!.length; i++){
            line.push(await imgToBase64(files![i]))
        }
        // console.log(line)
        // })

        // console.log(reader.readAsBinaryString(file))
    }
    const imgToBase64 = async(file:any) => {
        let reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = function () {
            // console.log(reader.result);
            setImages([...images, reader.result])
            // return reader.result;
            // setThumbnail(reader.result)
        };
        reader.onerror = function (error) {
            console.log('Error: ', error);
        };
    }
    useEffect(() => {
        dispatch(fetch_all_categories())
        dispatch(fetch_all_brands())
    }, [])
    useEffect(() => {
        images.forEach((item:any) => {
            console.log(item)
        })
    }, [images])
    return (
        <div className={'container-fluid'}>
            <div className={'col-md-5 col-lg-4 m-auto'}>
                <div className={'my-5 border border-1 py-3'}>
                    <div className={'w-100 border-bottom border-1 py-3'}>
                        <h5 className="text-center">Wear | New ware</h5>
                        {/*<p className={"text-danger text-center"}>{server_errors ? server_errors : ""}</p>*/}
                    </div>
                    <form onSubmit={handleSubmit(submitHandler)}>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="Name" className="form-label">Name</label>
                            <input type="text" className="form-control" id="Name"/>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="Brand" className="form-label">Brand</label>
                            <select className={'form-select'} id="Brand">
                                <option></option>
                                {brands?.map(item => (
                                    <option value={item.id}>{item.name}</option>
                                ))}
                            </select>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="Brand" className="form-label">Category</label>
                            <select className={'form-select'} id="category">
                                <option></option>
                                {categories?.map(item => (
                                    <option value={item.id}>{item.name}</option>
                                ))}
                            </select>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="Size" className="form-label">Sizes</label>
                            <input type="text" className="form-control" id="Size"/>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="Price" className="form-label">Price</label>
                            <input type="text" className="form-control" id="Price"/>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="OldPrice" className="form-label">Old price</label>
                            <input type="text" className="form-control" id="OldPrice"/>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="IsDiscount" className="form-label me-2">Is discount</label>
                            <input className="form-check-input" type="checkbox" value="" id="IsDiscount"/>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="CountInStorage" className="form-label">Count in storage</label>
                            <input className="form-control" type="number" value="" id="CountInStorage"/>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="thumbnail" className="form-label">Thumbnail</label>
                            <input onChange={(e:any) => selectThumbnailHandler(e)} className="form-control" type="file" id="thumbnail"/>
                        </div>
                        <div className="mt-2 mb-3 px-5">
                            <label htmlFor="Images" className="form-label">Images</label>
                            <input multiple={true} onChange={(e:any) => selectImagesHandler(e)} className="form-control" type="file" id="Images"/>
                        </div>
                        <div className={'px-5'}>
                            <button type="submit" className="btn btn-primary">Submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default NewWarePage;
