import React, {useEffect} from 'react';
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {get_orders} from "../../redux/action_creators/ware_action_creator";
import {Accordion} from "react-bootstrap";

const CabinetOrders = () => {
    const dispatch = useAppDispatch()
    const orders = useAppSelector(state => state.wareReducer.orders.orders)
    useEffect(() => {
        dispatch(get_orders())
    }, [ ])
    return (
        <div className={'w-75 mt-4 mx-auto'}>
            <Accordion defaultActiveKey="0">
                {orders.map(item => (
                <>
                    <Accordion.Item eventKey={`${item.orderId}`}>
                        <Accordion.Header>Order â„–{item.orderId} | {item.isConfirmed ? <button className={'ms-1 btn btn-success text-white'}>Confirmed</button> : <button className={'ms-1 btn btn-danger text-white p-1'}>Not confirmed</button>}</Accordion.Header>
                        <Accordion.Body>
                            <table className={'table'}>
                                <thead>
                                    <tr>
                                        <th>
                                            WareName
                                        </th>
                                        <th>
                                            Sizes
                                        </th>
                                        <th>
                                            Size
                                        </th>
                                        <th>
                                            Price
                                        </th>
                                        <th>
                                            CountInStorage
                                        </th>
                                        <th>
                                            Count
                                        </th>
                                    </tr>
                                </thead>
                                <tbody>
                                {item.orderWares.map(ware => (
                                    <tr>
                                        <td>
                                            {ware.wareName}
                                        </td>
                                        <td>
                                            {ware.sizes}
                                        </td>
                                        <td>
                                            {ware.size}
                                        </td>
                                        <td>
                                            {ware.price}
                                        </td>
                                        <td>
                                            {ware.countInStorage}
                                        </td>
                                        <td>
                                            {ware.count}
                                        </td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>

                        </Accordion.Body>
                    </Accordion.Item>
                </>
                ))}
            </Accordion>
        </div>
    );
};

export default CabinetOrders;
