import React, {useEffect, useState} from 'react';
import {
    CaretDown,
    CaretUp, EmojiExpressionless,
    Google, Heart, Heartbreak,
    HeartFill,
    Mailbox,
    Person,
    PersonBadgeFill,
    PlusCircle
} from "react-bootstrap-icons";
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {sign_out} from "../../redux/action_creators/authorization_action_creator";
import {Button, ButtonGroup} from "react-bootstrap";
import {
    delete_ware,
    fetch_all_wares,
    fetch_discount_wares,
    fetch_discount_wares_authorized, fetch_favorite_wares, remove_from_favorite
} from "../../redux/action_creators/ware_action_creator";
import {Link} from "react-router-dom";
import CabinetAccount from "./CabinetAccount";

const Cabinet = () => {
    const user = useAppSelector(state => state.authorizationReducer.user)
    const allWares = useAppSelector(state => state.wareReducer.allWares.wares)
    const favoriteWares = useAppSelector(state => state.wareReducer.favoriteWares)
    const [sortField, setSortField] = useState({value: "Name", isReverse: false})
    const [filter, setFilter] = useState("")
    const dispatch = useAppDispatch()
    const [currentPage, setCurrentPage] = useState('account')
    const signOutHandler = () => {
        dispatch(sign_out())
    }

    useEffect(() => {
        fetch_all()
    }, [sortField, filter])

    const fetch_all = () => {
        dispatch(fetch_all_wares({sortBy: {value: sortField.value, isReverse: sortField.isReverse}, filter: filter === "" ? null : filter}))
    }
    const fetch_favorite = () => {
        dispatch(fetch_favorite_wares())
    }

    useEffect(() => {
        if (currentPage === 'wares')
            dispatch(fetch_all_wares())
        if (currentPage === 'favorites')
            dispatch(fetch_favorite_wares())
    }, [currentPage])
    //
    // useEffect(() => {
    //     console.log(wares)
    // }, [wares])
    const changeSortHandler = (field: string) => {
        setSortField({value: field, isReverse: field === sortField.value ? !sortField.isReverse : false})
    }
    return (
        <>
            <div className={"container"}>
                <h2 className={"text-center display-6 mt-5"}>Personal Account</h2>
                <p onClick={signOutHandler} style={{cursor: 'pointer'}}
                   className={"text-decoration-underline text-center"}>Sign Out</p>
            </div>
            <div className={"container"}>
                <div className={'text-center'}>
                    <ButtonGroup>
                        <Button onClick={() => setCurrentPage('account')}
                                className={`hover-white ${currentPage === 'account' ? 'opacity-75' : 'opacity-100'}`}
                                variant="primary">Account</Button>
                        <Button onClick={() => setCurrentPage('cart')}
                                className={`hover-white ${currentPage === 'cart' ? 'opacity-75' : 'opacity-100'}`}
                                variant="primary">Cart</Button>
                        <Button onClick={() => setCurrentPage('favorites')}
                                className={`hover-white ${currentPage === 'favorites' ? 'opacity-75' : 'opacity-100'}`}
                                variant="primary">Favorites</Button>
                        {user?.permissions?.includes(1) ? <>
                            <Button onClick={() => setCurrentPage('wares')}
                                    className={`hover-white ${currentPage === 'wares' ? 'opacity-75' : 'opacity-100'}`}
                                    variant="primary">Wares</Button>
                        </> : <></>}
                    </ButtonGroup>
                </div>
            </div>
            <div className={`container ${currentPage === 'account' ? 'd-block' : 'd-none'}`}>
                <CabinetAccount user={user}/>
            </div>
            <div className={`container-fluid ${currentPage === 'favorites' ? 'd-block' : 'd-none'}`}>
                <div className={`w-50 mx-auto mt-3`}>
                    {/*<p className={'text-center'}><Link to={'NewWare'}>New ware</Link></p>*/}
                    <table className="table table-borderless">
                        <tbody>
                        {favoriteWares.length > 0 ? favoriteWares.map(item => (
                            <tr>
                                <td>
                                    <Link className={'text-decoration-none'} to={`/Wares/${item.wareId}`}>
                                        <div className={'d-block position-relative'}>
                                            <div className={'row border border-2'}>
                                                <div className="col-3">
                                                    <img height={200} width={200}
                                                         src={new Image().src = item.thumbnail ?? ""}
                                                         alt=""/>
                                                    <div className={'position-absolute top-0 start-0 mt-2'}>
                                                        {item.isDiscount ? (
                                                            <p className="bg-danger px-2 py-1 text-white d-inline">-{100 - Math.floor(100 * item.price / item.oldPrice)}%</p>) : <></>}
                                                        {item.countInStorage < 10 ? (
                                                            <p className="bg-danger px-2 py-1 text-white d-inline">{item.countInStorage < 1 ? <>Ended</> : <>Ends</>}</p>) : <></>}
                                                    </div>
                                                </div>
                                                <div className="col">
                                                    <h4 className={'mt-4'}>{item.name}</h4>
                                                    <p className="card-text text-muted">{item.sizes}</p>
                                                    <p className="text-danger d-inline">{item.price} грн.</p>
                                                    {item.isDiscount ? <p className="text-muted d-inline">
                                                        <del>{item.oldPrice} грн.</del>
                                                    </p> : <></>}
                                                    {item.countInStorage > 0 ?
                                                        <></> :
                                                        <p className={'text-decoration-underline mt-1'}>Ended</p>}
                                                </div>
                                            </div>
                                            <button onClick={() => dispatch(remove_from_favorite(item.wareId!, () => fetch_favorite()))} className="btn position-absolute bottom-0 end-0"> Remove from favorite</button>
                                        </div>
                                    </Link>
                                </td>
                            </tr>
                        )) : <h3 className={'text-center mt-5'}>Oops, it's empty<Heartbreak width={64} height={64}/></h3>}
                        </tbody>
                    </table>
                </div>
            </div>
            <div className={`container-fluid ${currentPage === 'wares' ? 'd-block' : 'd-none'}`}>
                <div className={`w-50 mx-auto mt-3`}>
                    {/*<p className={'text-center'}><Link to={'NewWare'}>New ware</Link></p>*/}
                    <table className="table table-borderless">
                        <tbody>
                        <tr>
                            <td>
                                <Link to={'/NewWare'}>
                                    <div className={'row border border-2'}>
                                        <div className="col text-center">
                                            <PlusCircle width={200} height={200} className={'py-5'}/>
                                        </div>
                                    </div>
                                </Link>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <form className="d-flex" role="search">
                                    <input className="form-control me-2" type="search" placeholder="Search"/>
                                    <button onClick={(e:any) => {dispatch(fetch_all_wares({keyWords: e.currentTarget.previousElementSibling!.value}));e.preventDefault()}} className="btn btn-outline-success" type="submit">Search</button>
                                </form>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <div className={"text-center"}>
                                    <p>Sort {sortField.isReverse ? <CaretDown/> : <CaretUp/>}</p>
                                    <button style={{backgroundColor: sortField.value === "Name" ? "red" : ""}} onClick={() => changeSortHandler('Name')} className={"btn btn-primary"}>Name</button>
                                    <button style={{backgroundColor: sortField.value === "Price" ? "red" : ""}} onClick={() => changeSortHandler("Price")} className={"btn btn-primary"}>Price</button>
                                    <button style={{backgroundColor: sortField.value === "Sizes" ? "red" : ""}} onClick={() => changeSortHandler("Sizes")} className={"btn btn-primary"}>Sizes</button>
                                    <button style={{backgroundColor: sortField.value === "OldPrice" ? "red" : ""}} onClick={() => changeSortHandler("OldPrice")} className={"btn btn-primary"}>OldPrice</button>
                                </div>
                                <div className={"text-center mt-4"}>
                                    <p>Filter: {filter}</p>
                                    <button style={{backgroundColor: filter === "Discount" ? "red" : ""}} onClick={() => setFilter('Discount')} className={"btn btn-primary"}>Discount</button>
                                    <button style={{backgroundColor: filter === "Novelty" ? "red" : ""}} onClick={() => setFilter("Novelty")} className={"btn btn-primary"}>Novelty</button>
                                    <button onClick={() => setFilter("")} className={"btn btn-success"}>Clear</button>
                                </div>
                            </td>
                        </tr>
                        {allWares.map(item => (
                            <tr>
                                <td>
                                    <div className={'d-block position-relative'}>
                                        <div className={'row border border-2'}>
                                            <div className="col-3">
                                                <img height={200} width={200}
                                                     src={new Image().src = item.thumbnail ?? ""}
                                                     alt=""/>
                                                <div className={'position-absolute top-0 start-0 mt-2'}>
                                                    {item.isDiscount ? (
                                                        <p className="bg-danger px-2 py-1 text-white d-inline">-{100 - Math.floor(100 * item.price / item.oldPrice)}%</p>) : <></>}
                                                    {item.countInStorage < 10 ? (
                                                        <p className="bg-danger px-2 py-1 text-white d-inline">{item.countInStorage < 1 ? <>Ended</> : <>Ends</>}</p>) : <></>}
                                                </div>
                                            </div>
                                            <div className="col">
                                                <h4 className={'mt-4'}>{item.name}</h4>
                                                <p className="card-text text-muted">{item.sizes}</p>
                                                <p className="text-danger d-inline">{item.price} грн.</p>
                                                {item.isDiscount ? <p className="text-muted d-inline">
                                                    <del>{item.oldPrice} грн.</del>
                                                </p> : <></>}
                                                {item.countInStorage > 0 ?
                                                    <p className={'mt-1'}>{item.countInStorage} in storage</p> :
                                                    <p className={'text-decoration-underline mt-1'}>Ended</p>}
                                                <div className={'position-absolute bottom-0 end-0'}>
                                                    <button onClick={() => dispatch(delete_ware(item.id, () => dispatch(fetch_all_wares())))} className={'btn btn-danger py-1 mb-1'}>Delete</button>
                                                    <Link className={'btn btn-warning py-1 mb-1'}
                                                          to={`/Wares/${item.id}`}>Update</Link>
                                                    <Link className={'btn btn-primary py-1 mb-1'}
                                                          to={`/Wares/${item.id}`}>More</Link>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
};

export default Cabinet;
