import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {fetch_user_by_id} from "../../redux/action_creators/users_action_creator";
import {useForm} from "react-hook-form";
import Select from "react-select";

const CabinetUserEdit = () => {
    const {id} = useParams()
    const {user, isLoading} = useAppSelector(state => state.UsersReducer.getUserById)
    const permissionsList = useAppSelector(state => state.UsersReducer.permissions)
    const [permissionsOptions, setPermissionsOptions] = useState<{ label: string; value: string }[]>([]);
    const [currentPermissionsOptions, setCurrentPermissionsOptions] = useState<{ label: string; value: string }[]>([]);
    const [newPermissions, setPermissions] = useState()
    const dispatch = useAppDispatch()
    const {
        register,
        formState: {
            errors,
        },
        handleSubmit,
    } = useForm()
    useEffect(() => {
        let userPermissions: { label: string; value: string }[] = [];
        if (user != null){
            user!.permissions.forEach((permission) => {
                userPermissions = [
                    ...userPermissions,
                    { value: permission.toString(), label: permissionsList.find((p) => p.id == permission)?.name ?? "" },
                ];
            });
            setCurrentPermissionsOptions(userPermissions);
        }
    }, [user])
    useEffect(() => {
        if (permissionsList.length > 0) {
            permissionsList.forEach((item) =>
                setPermissionsOptions((prevState: any) => [
                    ...prevState,
                    {
                        value: item.id.toString(),
                        label: item.name,
                    },
                ])
            );
        }
    }, [permissionsList]);

    const onPermissionsChanged = (newValue: any) => {
        setCurrentPermissionsOptions(newValue);
        setPermissions((prevState) => {
            prevState = newValue.map((v: any) => Number(v.value));
            return prevState;
        });
    };
    useEffect(() => {
        dispatch(fetch_user_by_id(Number(id)))
    }, [id])
    return (
        <div>
            {isLoading ? <></> :
                <>
                    <form>
                        <div className={'mb-3'}>
                            <label className={'form-label'} htmlFor="username">Username</label>
                            <input id={'username'} className={'form-control'} type="text"/>
                        </div>
                        <div className={'mb-3'}>
                            <label className={'form-label'} htmlFor="name">Name</label>
                            <input id={'name'} className={'form-control'} type="text"/>
                        </div>
                        <div className={'mb-3'}>
                            <label className={'form-label'} htmlFor="surname">Surname</label>
                            <input id={'surname'} className={'form-control'} type="text"/>
                        </div>
                        <div className={'mb-3'}>
                            <label className={'form-label'} htmlFor="surname">Surname</label>
                            <input id={'surname'} className={'form-control'} type="text"/>
                        </div>
                        <div className={'mb-3'}>
                            <label className={'form-label'} htmlFor="email">Email</label>
                            <input id={'email'} className={'form-control'} type="text"/>
                        </div>
                        <div className={'mb-3'}>
                            <label className={'form-label'} htmlFor="age">Age</label>
                            <input id={'age'} className={'form-control'} type="number"/>
                        </div>
                        <div className={'mb-3'}>
                            <label className={'form-label'} htmlFor="adress">Adress</label>
                            <input id={'adress'} className={'form-control'} type="adress"/>
                        </div>
                        <div className={"mb-3"}>
                            <Select
                                value={currentPermissionsOptions}
                                onChange={onPermissionsChanged}
                                placeholder={"Permissions"}
                                isMulti={true}
                                options={permissionsOptions}
                            />
                        </div>
                    </form>
                </>
            }
        </div>
    );
};

export default CabinetUserEdit;
