import React, {useEffect} from 'react';
import {useParams} from "react-router-dom";
import {useDispatch} from "react-redux";
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {fetch_ware_by_id} from "../../redux/action_creators/ware_action_creator";
import {Carousel} from "react-bootstrap";

const WarePage = () => {
    const id = useParams()
    const dispatch = useAppDispatch()
    const {ware} = useAppSelector(state => state.wareReducer)

    useEffect(() => {
        if (id !== undefined){
            console.log(id)
            dispatch(fetch_ware_by_id(Number(id.id)))
        }
    }, [ ])

    return (
        <div className={'container'}>
            <div className="row">
                <div className="col-12 col-lg">
                    <Carousel variant={'dark'}>
                        <Carousel.Item>
                            <img
                                className="d-block w-50 text-center mx-auto"
                                src="https://static.staff-clothes.com/uploads/media/image_product/0002/68/93ee5869478d4f86bdba8e694077f236.jpeg"
                                alt="First slide"
                            />
                        </Carousel.Item>
                        <Carousel.Item>
                            <img
                                className="d-block w-50 text-center mx-auto"
                                src="https://static.staff-clothes.com/uploads/media/image_product/0002/68/93ee5869478d4f86bdba8e694077f236.jpeg"
                                alt="First slide"
                            />
                        </Carousel.Item>
                        <Carousel.Item>
                            <img
                                className="d-block w-50 text-center mx-auto"
                                src="https://static.staff-clothes.com/uploads/media/image_product/0002/68/93ee5869478d4f86bdba8e694077f236.jpeg"
                                alt="First slide"
                            />
                        </Carousel.Item>
                    </Carousel>
                </div>
                <div className="col">
                    <h1 className={'mt-5 text-center text-md'}>{ware?.name}</h1>
                    <hr className={'d-md-none'}/>
                    <h5 className={'text-center text-md'}>КАТЕГОРІЯ: {ware?.categoryId} БРЕНД:{ware?.brandId}</h5>
                    <hr className={'d-md-none'}/>
                    <h4 className={'text-center text-md'}>Ціна:{ware?.isDiscount ? <p className={'text-danger'}>{ware?.price}. <del className={'text-muted'}>{ware?.oldPrice}</del> грн</p> : <p>{ware?.price} грн</p>}</h4>
                </div>
            </div>
        </div>
    );
};

export default WarePage;
