import React, {useEffect} from 'react';
import {Route, Routes} from "react-router-dom";
import Welcome from "./pages/Welcome/Welcome";
import PageNotFound from "./pages/PageNotFound/PageNotFound";
import Header from "./pages/Layout/Header";
import {useAppDispatch, useAppSelector} from "./hooks/redux";
import Cabinet from "./pages/Cabinet/Cabinet";
import LoginPage from "./pages/SignIn/Login";
import RegistrationPage from "./pages/SignIn/Registration";
import {refresh_token} from "./redux/action_creators/authorization_action_creator";
import Discounts from "./pages/Discounts/Discounts";
import * as THREE from 'three'
import {MeshBasicNodeMaterial} from "three/examples/jsm/nodes/materials/Materials";
function App() {
    const dispatch = useAppDispatch()
    const {isAuthorized} = useAppSelector(state => state.authorizationReducer)

    useEffect(() => {
        dispatch(refresh_token())
        const canvas:any = document.getElementById('canva')
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera( 75, window.innerWidth / window.innerHeight, 0.1, 1000 );
        const renderer = new THREE.WebGLRenderer({
            canvas: canvas,
            antialias: true
        });
        const geometry = new THREE.BoxGeometry( 1, 1, 1 );
        const material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
        const cube = new THREE.Mesh( geometry, material );
        scene.add( cube );

        camera.position.z = 5;
        document.body.appendChild( renderer.domElement );
        renderer.setSize( window.innerWidth, window.innerHeight );
    }, [ ])

    useEffect(() => {
        console.log(isAuthorized)
    }, [isAuthorized])

    return (
        <>
            {/*<Header/>*/}
            {/*<Routes>*/}
            {/*    <Route path={"/"} element={<Welcome/>}/>*/}
            {/*    <Route path={"/Cabinet"} element={isAuthorized ? <Cabinet/> : <LoginPage/>}/>*/}
            {/*    <Route path={"/Discounts"} element={<Discounts/>}/>*/}
            {/*    <Route path={"/SignIn"} element={isAuthorized ? <Cabinet/> : <RegistrationPage/>}/>*/}
            {/*    <Route path={"*"} element={<PageNotFound/>}/>*/}
            {/*</Routes>*/}

        </>
    );
}

export default App;
