import React, {useEffect, useState} from 'react';
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {get_orders, update_order} from "../../redux/action_creators/ware_action_creator";
import {Accordion} from "react-bootstrap";
import CabinetOrderItem from "./CabinetOrderItem";
import {CaretDown, CaretUp} from "react-bootstrap-icons";
import {fetch_all_users} from "../../redux/action_creators/users_action_creator";

const CabinetOrders = () => {
    const dispatch = useAppDispatch()
    const orders = useAppSelector(state => state.wareReducer.orders.orders)
    const [filter, setFilter] = useState<string | null>(null)
    useEffect(() => {
        dispatch(get_orders())
    }, [ ])

    useEffect(() => {
        if (filter === "true"){
            dispatch(get_orders(true))
        }
        if (filter === "false"){
            dispatch(get_orders(false))
        }
        if (filter === ""){
            dispatch(get_orders())
        }
    }, [filter])
    return (
        <div className={'w-75 mt-4 mx-auto'}>
            <div className={'row'}>
                <div className="col">
                    <form className="d-flex input-group-sm mt-3" role="search">
                        <input className="form-control py-0" type="search" placeholder="Search by name"/>
                        <button onClick={(e: any) => {
                            dispatch(fetch_all_users(e.currentTarget.previousElementSibling!.value ?? null));
                            e.preventDefault()
                        }} className="btn btn-outline-success" type="submit">Search
                        </button>
                        <button onClick={(e:any) => {dispatch(fetch_all_users());e.preventDefault();e.currentTarget.previousElementSibling!.previousElementSibling!.value = ""}} className={'btn btn-outline-primary'}>Reset</button>
                    </form>
                </div>
                <div className="col">
                    <div className="mb-4 w-50 mx-auto">
                        <select onChange={e => setFilter(e.currentTarget.value)}
                                className={'form-select'}>
                            <option selected value="">All</option>
                            <option value="true">Confirmed</option>
                            <option value="false">Not confirmed</option>
                        </select>
                    </div>
                </div>
            </div>
            <Accordion defaultActiveKey="0">
                {orders.map(item => (
                    <CabinetOrderItem item={item} />
                ))}
            </Accordion>
        </div>
    );
};

export default CabinetOrders;
