import React, {FC, useEffect, useState} from 'react';
import {Button, Modal} from "react-bootstrap";
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {Heartbreak} from "react-bootstrap-icons";
import ReactDOM from 'react-dom'
import {useDispatch} from "react-redux";
import {fetch_favorite_wares} from "../../redux/action_creators/ware_action_creator";

interface IFavoriteWares{
    show: boolean,
    closeHandler: () => void,
}
const FavoriteWaresModal:FC<IFavoriteWares> = ({ show, closeHandler}) => {
    const dispatch = useAppDispatch()
    const {isAuthorized} = useAppSelector(state => state.authorizationReducer)
    const {favoriteWares} = useAppSelector(state => state.wareReducer)
    useEffect(() => {
        dispatch(fetch_favorite_wares())
    }, [ ])
    // useEffect(() => {
    //     // console.log(favoriteWares)
    // }, [favoriteWares])
    if (!show) return null
    return ReactDOM.createPortal(
        <Modal show={show} onHide={closeHandler}>
            <Modal.Header closeButton>
                <Modal.Title>Favorite wares</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {!isAuthorized ? <h3 className={'text-center my-4'}>Ви не авторизовані <Heartbreak className={'mb-1'}/></h3> :
                <>
                    {favoriteWares.map(item => (
                           <>
                               <div className="card" style={{"width": "18rem;"}}>
                                   <img width={"100px"} height={"100px"} src={"https://www.staff-clothes.com/_next/image/?url=https%3A%2F%2Fstatic.staff-clothes.com%2Fmedia%2Fcache%2Fimage_product_desktop_catalog%2Fimage_product%2F0002%2F73%2Fe3b0187358014ffeadec729c7f0beebf.jpeg&w=1920&q=75"} className="card-img-top" alt="..."/>
                                       <div className="card-body">
                                           <h5 className="card-title">{item.name}</h5>
                                           <p className="card-text">{item.sizes}</p>
                                           <p className="card-text">{item.price} грн.</p>
                                           {item.isDiscount ? <p className="card-text"><del>{item.oldPrice} грн.</del></p> : <></>}
                                           <a href="#" className="btn btn-primary">More</a>
                                       </div>
                               </div>
                           </>
                        )
                    )}
                </>
                }

            </Modal.Body>
        </Modal>,
        document.getElementById('root') as HTMLElement
    );
};

export default FavoriteWaresModal;
