import React, {FC, useRef, useState} from 'react';
import {IWare} from "../types/ware";
import "./WareMockup.module.css"

interface IWareMockupProps{
    item: IWare
}
const WareMockup:FC<IWareMockupProps> = ({item}) => {
    const [likes, setLikes] = useState(100);
    const [isClicked, setIsClicked] = useState(false);

    const handleClick = () => {
        if (isClicked) {
            setLikes(likes - 1);
        } else {
            setLikes(likes + 1);
        }
        setIsClicked(!isClicked);
    };
    return (
        <div key={item.id} className="card shadow mt-4 me-lg-4 col-12 col-md-4 mx-auto mx-lg-0" style={{"width": "18rem"}}>
            {item.isDiscount ? (<p className="position-absolute top-0 start-0 bg-danger px-2 py-1 m-2 text-white">-{100 - Math.floor(100 * item.price / item.oldPrice)}%</p>) : <></>}
            {item.countInStorage < 10 ? (<p className="position-absolute top-0 end-0 bg-danger px-2 py-1 m-2 text-white">Закінчується</p>) : <></>}
            <div id="example"></div>
            <img
                src="https://www.staff-clothes.com/_next/image/?url=https%3A%2F%2Fstatic.staff-clothes.com%2Fmedia%2Fcache%2Fimage_product_desktop_catalog%2Fimage_product%2F0002%2F73%2Fe3b0187358014ffeadec729c7f0beebf.jpeg&w=1920&q=75"
                className="card-img-top blur-elem" alt="..."/>
            <div className="card-body">
                <h5 className="card-title"><p className={'d-inline'}>{item.name}</p></h5>
                <p className="card-text text-muted">{item.sizes}</p>
                <p className="text-danger d-inline">{item.price} грн.</p>
                <p className="text-muted d-inline">
                    <del>{item.oldPrice} грн.</del>
                </p>
            </div>
        </div>
    );
};

export default WareMockup;
