import React, {useEffect} from 'react';
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import {get_orders, update_order} from "../../redux/action_creators/ware_action_creator";
import {Accordion} from "react-bootstrap";

const CabinetOrders = () => {
    const dispatch = useAppDispatch()
    const orders = useAppSelector(state => state.wareReducer.orders.orders)
    useEffect(() => {
        dispatch(get_orders())
    }, [ ])
    return (
        <div className={'w-75 mt-4 mx-auto'}>
            <Accordion defaultActiveKey="0">
                {orders.map(item => (
                <>
                    <Accordion.Item eventKey={`${item.orderId}`}>
                        <Accordion.Header>
                                â„–{item.orderId} | {item.status} | {item.isConfirmed ? <button onClick={e => {dispatch(update_order(item.orderId, "Canceled", false, () => {
                                dispatch(get_orders())
                            }));e.stopPropagation()}} className={'mx-1 btn btn-success text-white py-0 px-1'}>Click to cancel</button> : <button onClick={e => {dispatch(update_order(item.orderId, "Confirmed", true, () => {
                                dispatch(get_orders())
                            }));e.stopPropagation()}} className={'mx-1 btn btn-danger text-white py-0 px-1'}>Click to confirm</button>}
                            <button onClick={e => e.stopPropagation()} className={'btn btn-primary px-1 py-0 position-absolute end-0 me-5'}>Edit</button>
                        </Accordion.Header>
                        <Accordion.Body>
                            <table className={'table'}>
                                <thead>
                                    <tr>
                                        <th>
                                            WareName
                                        </th>
                                        <th>
                                            Sizes
                                        </th>
                                        <th>
                                            Size
                                        </th>
                                        <th>
                                            Price
                                        </th>
                                        <th>
                                            CountInStorage
                                        </th>
                                        <th>
                                            Count
                                        </th>
                                    </tr>
                                </thead>
                                <tbody>
                                {item.orderWares.map(ware => (
                                    <tr>
                                        <td>
                                            {ware.wareName}
                                        </td>
                                        <td>
                                            {ware.sizes}
                                        </td>
                                        <td>
                                            {ware.size}
                                        </td>
                                        <td>
                                            {ware.price}
                                        </td>
                                        <td>
                                            {ware.countInStorage}
                                        </td>
                                        <td>
                                            {ware.count}
                                        </td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>

                        </Accordion.Body>
                    </Accordion.Item>
                </>
                ))}
            </Accordion>
        </div>
    );
};

export default CabinetOrders;
