import {AppDispatch} from "../store";
import {wareReducer} from "../reducers/wareReducer";
import axios from "axios";
import {GraphQlEndpoint} from "../../global_variables";
import {authorizationReducer} from "../reducers/authorizationReducer";

export const fetch_discount_wares = (categoryId: number | null, countOfRecords: number = 20, sortBy: string = "Id") => async (dispatch: AppDispatch) => {
    dispatch(wareReducer.actions.GET_WARE())
    try{
        const graphqlQuery = {
            "query": `
                query getdware{
                  ware{
                    getDiscountWares(settings: {
                      categoryId: null,
                      countOfRecords: 20,
                      sortParam: {
                        isReverse: false,
                        value: "price"
                      }
                    }){
                      errors
                      statusCode
                      wares{
                        id
                        name
                        brandId
                        categoryId
                        description
                        sizes
                        price
                        oldPrice
                        isDiscount
                        countInStorage
                      }
                    }
                  }
                }
            `
        }
        let response = await axios({
            url: GraphQlEndpoint,
            method: 'post',
            withCredentials: true,
            headers: {
                "content-type": "application/json",
                "Authorization": `Bearer ${localStorage.getItem('accessToken')}`,
            },
            data: graphqlQuery
        })
        let r = response.data.data.ware.getDiscountWares
        if (r.statusCode === 200){
            dispatch(wareReducer.actions.GET_WARE_SUCCESS(r.wares))
        }else{
            dispatch(wareReducer.actions.GET_WARE_ERROR(r.errors))
        }

    }
    catch (e){
        dispatch(wareReducer.actions.GET_WARE_ERROR(e))
        console.log(e)
    }
}

export const fetch_all_wares = () => async (dispatch: AppDispatch) => {
    dispatch(wareReducer.actions.GET_WARE())
    try{
        const graphqlQuery = {
            "query": `
                query getaware{
                  ware{
