import React, {FC, useEffect, useState} from 'react';
import {Link} from "react-router-dom";
import {CaretDown, CaretUp, PlusCircle} from "react-bootstrap-icons";
import {delete_ware, fetch_all_wares} from "../../redux/action_creators/ware_action_creator";
import {useAppDispatch} from "../../hooks/redux";
import {IWare} from "../../types/ware";

interface ICabinetWares {
    allWares: IWare[]
}
const CabinetWares:FC<ICabinetWares> = ({allWares}) => {
    const dispatch = useAppDispatch()
    const [sortField, setSortField] = useState({value: "Name", isReverse: false})
    const [filter, setFilter] = useState("")
    useEffect(() => {
        fetch_all()
    }, [sortField, filter])

    const fetch_all = () => {
        dispatch(fetch_all_wares({sortBy: {value: sortField.value, isReverse: sortField.isReverse}, filter: filter === "" ? null : filter}))
    }
    const changeSortHandler = (field: string) => {
        setSortField({value: field, isReverse: field === sortField.value ? !sortField.isReverse : false})
    }
    return (
        <div className={`w-50 mx-auto mt-3`}>
            {/*<p className={'text-center'}><Link to={'NewWare'}>New ware</Link></p>*/}
            <table className="table table-borderless">
                <tbody>
                <tr>
                    <td>
                        <Link to={'/NewWare'}>
                            <div className={'row border border-2'}>
                                <div className="col text-center">
                                    <PlusCircle width={200} height={200} className={'py-5'}/>
                                </div>
                            </div>
                        </Link>
                    </td>
                </tr>
                <tr>
                    <td>
                        <form className="d-flex" role="search">
                            <input className="form-control me-2" type="search" placeholder="Search"/>
                            <button onClick={(e:any) => {dispatch(fetch_all_wares({keyWords: e.currentTarget.previousElementSibling!.value}));e.preventDefault()}} className="btn btn-outline-success" type="submit">Search</button>
                        </form>
                    </td>
                </tr>
                <tr>
                    <td>
                        <div className={"text-center"}>
                            <p>Sort {sortField.isReverse ? <CaretDown/> : <CaretUp/>}</p>
                            <button style={{backgroundColor: sortField.value === "Name" ? "red" : ""}} onClick={() => changeSortHandler('Name')} className={"btn btn-primary"}>Name</button>
                            <button style={{backgroundColor: sortField.value === "Price" ? "red" : ""}} onClick={() => changeSortHandler("Price")} className={"btn btn-primary"}>Price</button>
                            <button style={{backgroundColor: sortField.value === "Sizes" ? "red" : ""}} onClick={() => changeSortHandler("Sizes")} className={"btn btn-primary"}>Sizes</button>
                            <button style={{backgroundColor: sortField.value === "OldPrice" ? "red" : ""}} onClick={() => changeSortHandler("OldPrice")} className={"btn btn-primary"}>OldPrice</button>
                        </div>
                        <div className={"text-center mt-4"}>
                            <p>Filter: {filter}</p>
                            <button style={{backgroundColor: filter === "Discount" ? "red" : ""}} onClick={() => setFilter('Discount')} className={"btn btn-primary"}>Discount</button>
                            <button style={{backgroundColor: filter === "Novelty" ? "red" : ""}} onClick={() => setFilter("Novelty")} className={"btn btn-primary"}>Novelty</button>
                            <button onClick={() => setFilter("")} className={"btn btn-success"}>Clear</button>
                        </div>
                    </td>
                </tr>
                {allWares.map(item => (
                    <tr>
                        <td>
                            <div className={'d-block position-relative'}>
                                <div className={'row border border-2'}>
                                    <div className="col-3">
                                        <img height={200} width={200}
                                             src={new Image().src = item.thumbnail ?? ""}
                                             alt=""/>
                                        <div className={'position-absolute top-0 start-0 mt-2'}>
                                            {item.isDiscount ? (
                                                <p className="bg-danger px-2 py-1 text-white d-inline">-{100 - Math.floor(100 * item.price / item.oldPrice)}%</p>) : <></>}
                                            {item.countInStorage < 10 ? (
                                                <p className="bg-danger px-2 py-1 text-white d-inline">{item.countInStorage < 1 ? <>Ended</> : <>Ends</>}</p>) : <></>}
                                        </div>
                                    </div>
                                    <div className="col">
                                        <h4 className={'mt-4'}>{item.name}</h4>
                                        <p className="card-text text-muted">{item.sizes}</p>
                                        <p className="text-danger d-inline">{item.price} грн.</p>
                                        {item.isDiscount ? <p className="text-muted d-inline">
                                            <del>{item.oldPrice} грн.</del>
                                        </p> : <></>}
                                        {item.countInStorage > 0 ?
                                            <p className={'mt-1'}>{item.countInStorage} in storage</p> :
                                            <p className={'text-decoration-underline mt-1'}>Ended</p>}
                                        <div className={'position-absolute bottom-0 end-0'}>
                                            <button onClick={() => dispatch(delete_ware(item.id, () => dispatch(fetch_all_wares())))} className={'btn btn-danger py-1 mb-1'}>Delete</button>
                                            <Link className={'btn btn-warning py-1 mb-1'}
                                                  to={`/Wares/${item.id}`}>Update</Link>
                                            <Link className={'btn btn-primary py-1 mb-1'}
                                                  to={`/Wares/${item.id}`}>More</Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default CabinetWares;
