import {AppDispatch} from "../store";
import {wareReducer} from "../reducers/wareReducer";
import axios from "axios";
import {GraphQlEndpoint} from "../../global_variables";
import {authorizationReducer} from "../reducers/authorizationReducer";

interface ISortType{
    value: string,
    isReverse: boolean
}
export const fetch_discount_wares = (sortBy: ISortType | null = {isReverse: false, value: "Id"}, categoryId?: number | null, countOfRecords: number | null = 20) => async (dispatch: AppDispatch) => {
    dispatch(wareReducer.actions.GET_DISCOUNT_WARES())
    try{
        const graphqlQuery = {
            "query": `
                query getdware{
                  ware{
                    getDiscountWares(settings: {
                      categoryId: ${categoryId ?? "null"},
                      countOfRecords: ${countOfRecords},
                      sortParam: {
                        isReverse: ${sortBy?.isReverse},
                        value: "${sortBy?.value}"
                      }
                    }){
                      errors
                      statusCode
                      wares{
                        id
                        name
                        brandId
                        categoryId
                        description
                        sizes
                        price
                        oldPrice
                        isDiscount
                        countInStorage
                      }
                    }
                  }
                }
            `
        }
        let response = await axios({
            url: GraphQlEndpoint,
            method: 'post',
            withCredentials: true,
            headers: {
                "content-type": "application/json",
                "Authorization": `Bearer ${localStorage.getItem('accessToken')}`,
            },
            data: graphqlQuery
        })
        let r = response.data.data.ware.getDiscountWares
        if (r.statusCode === 200){
            dispatch(wareReducer.actions.GET_DISCOUNT_WARES_SUCCESS(r.wares))
        }else{
            dispatch(wareReducer.actions.GET_DISCOUNT_WARES_ERROR(r.errors))
        }

    }
    catch (e){
        dispatch(wareReducer.actions.GET_DISCOUNT_WARES_ERROR(e))
        console.log(e)
    }
}
interface Ifetch_all_wares{
    sortBy: ISortType | null,
    categoryId?: number | null,
    countOfRecords: number | null,
    filter?: string | null
}
export const fetch_all_wares = (settings: Ifetch_all_wares) => async (dispatch: AppDispatch) => {
    dispatch(wareReducer.actions.GET_WARES())
    try{
        const graphqlQuery = {
            "query": `
                query getaware{
                  ware{
                    getAllWares(settings: {
                      categoryId: ${categoryId ?? "null"},
                      countOfRecords: ${countOfRecords},
                      sortParam: {
                        isReverse: ${sortBy?.isReverse},
                        value: "${sortBy?.value}"
                      },
                      filter: ${filter}
                    }){
                      errors
                      statusCode
                      wares{
                        id
                        name
                        brandId
                        categoryId
                        description
                        sizes
                        price
                        oldPrice
                        isDiscount
                        countInStorage
                      }
                    }
                  }
                }
            `
        }
        let response = await axios({
            url: GraphQlEndpoint,
            method: 'post',
            withCredentials: true,
            headers: {
                "content-type": "application/json",
                "Authorization": `Bearer ${localStorage.getItem('accessToken')}`,
            },
            data: graphqlQuery
        })
        let r = response.data.data.ware.getAllWares
        if (r.statusCode === 200){
            dispatch(wareReducer.actions.GET_WARES_SUCCESS(r.wares))
        }else{
            dispatch(wareReducer.actions.GET_WARES_ERROR(r.errors))
        }

    }
    catch (e){
        dispatch(wareReducer.actions.GET_WARES_ERROR(e))
        console.log(e)
    }
}

export const fetch_ware_by_id = (id: number) => async (dispatch: AppDispatch) => {
    dispatch(wareReducer.actions.GET_WARE_BY_ID())
    try{
        const graphqlQuery = {
            "query": `
                query getwarebi{
                  ware{
                    getWareById(wareId: ${id}){
                      errors
                      statusCode
                      ware{
                        id
                        name
                        brandId
                        categoryId
                        description
                        sizes
                        price
                        oldPrice
                        isDiscount
                        countInStorage
                      }
                    }
                  }
                }
            `
        }
        let response = await axios({
            url: GraphQlEndpoint,
            method: 'post',
            withCredentials: true,
            headers: {
                "content-type": "application/json",
                "Authorization": `Bearer ${localStorage.getItem('accessToken')}`,
            },
            data: graphqlQuery
        })
        let r = response.data.data.ware.getWareById
        if (r.statusCode === 200){
            dispatch(wareReducer.actions.GET_WARE_BY_ID_SUCCESS(r.ware))
        }else{
            dispatch(wareReducer.actions.GET_WARE_BY_ID_ERROR(r.errors))
        }

    }
    catch (e){
        dispatch(wareReducer.actions.GET_WARE_BY_ID_ERROR(e))
        console.log(e)
    }
}
