export const confirmOrder = (callback: () => void) => async (dispatch: AppDispatch) => {
    try {
        const graphqlQuery = {
            "query": `
                mutation confirmO {
                  ware {
                    confirmOrder {
                      errors
                      statusCode
                      wares {
                        basketId
                        count
                          id
                          name
                          brandId
                          categoryId
                          description
                          sizes
                          price
                          oldPrice
                          isDiscount
                          countInStorage
                      }
                    }
                  }
                }
            `
        }
        let response = await axios({
            url: GraphQlEndpoint,
            method: 'post',
            withCredentials: true,
            headers: {
                "content-type": "application/json",
                "Authorization": `Bearer ${localStorage.getItem('accessToken')}`,
            },
            data: graphqlQuery
        })
        let r = response.data.data.ware.confirmOrder
        callback()
    } catch (e) {
        console.log(e)
    }
}
